import { CardBody, CardContainer, CardItem } from "@/components/ui/3d-card";
import BackHome from "@/components/ui/backhome";
import Image from "next/image";
import React from "react";

const Images = () => {
  return (
    <div className="min-h-screen flex justify-center items-center flex-col p-5 gap-y-[5vh]">
      <BackHome/>
      {/* We are using a header here since the screen reader will be able to decipher 
          it as a header and hence speak it. This will help the blind in knowing which tab
           they are on */}
      <h1 className="text-lg bg-blue-600 text-white px-5 py-3 rounded-lg w-[95%] sm:w-[70%]" tabIndex={0}>
        We need to associate more information with an image so that the screen reader picks that up when the image is focused. 
        Try navigating through these images using Tab key or focus separately to understand the difference.
        Keep the screen reader on and for better understanding, view the code on Github.
      </h1>
      <div className="w-[80vw] flex-wrap flex gap-x-[5vw] justify-center items-center">
        <ThreeDWrapper>
          {/* The image without proper alt text so the screen reader 
              will stay silent which is bad UX for blind people */}
          <Image
            tabIndex={0}
            height={300}
            width={300}
            src="data:image/jpeg;base64,"
            alt=""
          />
          <CardItem as="p"
          translateZ="60"
            className="text-base max-w-sm mt-2 text-neutral-300">
            This image element has got no alt text associated neither any aria-label and hence screen reader will stay silent
          </CardItem>
        </ThreeDWrapper>

        <ThreeDWrapper>
          {/* we have proper alt text so the screen reader will read that on selecting this image */}
          <Image
            tabIndex={0}
            height={300}
            width={300}
            src="data:image/jpeg;base64,"
            alt="Sunrise is the one of the most beautiful natural phenomenon."
          />
          <CardItem as="p"
          translateZ="60"
            className="text-base max-w-sm mt-2 text-neutral-300">
            This image element has proper alt text associated so the screen reader will read that on focusing the image
          </CardItem>
        </ThreeDWrapper>

        <ThreeDWrapper>
          {/* same here as above, its just that on hovering over the image, the title will also appear */}
          <Image
            tabIndex={0}
            height={300}
            width={300}
            src="data:image/jpeg;base64,"
            alt="Sunrise is the one of the most beautiful natural phenomenon."
            title="Picture of Sunrise"
          />
          <CardItem as="p"
          translateZ="60"
            className="text-base max-w-sm mt-2 text-neutral-300">
            This image has a proper alt text as well as a title and hence on hovering over it, the title will appear
          </CardItem>
        </ThreeDWrapper>
        <ThreeDWrapper>
          {/* here what we are doing is that we have some text inside <p> whose id="image-label". Now
                  since we might not want to repeat it so we did is tell the <Image/> that it is labeled
                  by the <p>'s text using aria-labelledby and the id of <p>. Accordingly, screen reader
                  will read that out */}
          <Image
            tabIndex={0}
            height={300}
            width={300}
            src="data:image/jpeg;base64,"
            aria-labelledby="image-label"
            alt=""
            title="Picture of Sunrise"
          />
          <CardItem as="p"
            id="image-label"
            translateZ="50"
            className="text-xl font-bold text-white mt-2">
            Sunrise is the one of the most beautiful natural phenomenon.
          </CardItem>
                    <CardItem as="p"
          translateZ="60"
            className="text-base max-w-sm mt-2 text-neutral-300">
            The image is associated with the above label since using aria-label we have associated it with that and hence the screen reader will mention the label on focusing the image
          </CardItem>
        </ThreeDWrapper>
        <ThreeDWrapper>
          {/* similar as above just using figure and figcaption */}
          <figure>
            <Image
              height={300}
              width={300}
              src="data:image/jpeg;base64,"
              aria-describedby="image-caption"
              alt=""
              title="Picture of Sunrise"
            />
                      <CardItem as="p"
            translateZ="50"
            className="text-xl font-bold  text-white mt-2">
            <figcaption id="image-caption">
              Sunrise is the one of the most beautiful nature phenomenon.
            </figcaption>
            </CardItem>
            <CardItem as="p"
          translateZ="60"
            className="text-base max-w-sm mt-2 text-neutral-300">
            The image is associated with the above label since using aria-describedby. We are using figure and figcaption here. Make sure to check out the code on github.
          </CardItem>
          </figure>
        </ThreeDWrapper>
      </div>
    </div>
  );
};

export default Images;


const ThreeDWrapper = ({ children }) => (
  <CardContainer className="inter-var w-[300px]">
    <CardBody className="relative group/card  hover:shadow-2xl hover:shadow-emerald-500/[0.1] bg-black border-black/[0.1] w-auto sm:w-[30rem] h-auto rounded-xl p-6 border  ">
      {children}
    </CardBody>
    </CardContainer>
)